import { call, takeEvery, put, select } from "redux-saga/effects";

import { MOVIES } from "../constants";
import { setMovies } from "../actions";

import { fetchPopularMovies } from "../api/index";

export const getKeyword = (state) => state.keyword;

export function* handleMoviesLoad() {
  const keyword = yield select(getKeyword);
  const movies = yield call(fetchPopularMovies);
  yield put(setMovies(movies));
}

export default function* watchMoviesLoad() {
  yield takeEvery(MOVIES.FETCH, handleMoviesLoad);
}
