import { takeEvery } from "redux-saga/effects";

import { MOVIES } from "../constants/index";

function* doSomething() {
  console.log("do something");
  yield "hello";
}

function* rootSaga() {
  yield takeEvery(MOVIES.FETCH_MOVIES, doSomething);
}

export default rootSaga;
