import React, { Fragment, useEffect } from "react";
import { connect } from "react-redux";

import { loadMovies, changeSearchKeyword } from "../actions";
import { getMovie } from "../selectors";

const Movie = ({
  loadMovies,
  changeSearchKeyword,
  movies,
  isLoading,
  error,
  keyword,
}) => {
  useEffect(() => {
    loadMovies();
  }, []);

  return (
    <Fragment>
      <div>a movie</div>
      {console.log(isLoading, error, keyword, changeSearchKeyword)}
      {movies && JSON.stringify(movies)}
      {/*<button onClick={() => console.log("hello", movies)}>hello</button>*/}
      <button onClick={() => changeSearchKeyword("hello")}>
        keyword change
      </button>
    </Fragment>
  );
};

const mapStateToProps = ({ isLoading, movies, error, keyword }) => ({
  movies,
  isLoading,
  error,
  keyword,
});

const mapDispatchToProps = (dispatch) => ({
  loadMovies: () => dispatch(loadMovies()),
  changeSearchKeyword: () => dispatch(changeSearchKeyword("vfdgda")),
});

export default connect(mapStateToProps, mapDispatchToProps)(Movie);
