import { MOVIES } from "../constants/index";

const loadMovies = () => ({
  type: MOVIES.FETCH,
});

const setMovies = (movies) => ({
  type: MOVIES.FETCH_SUCCESS,
  movies,
});

const changeSearchKeyword = (keyword) => ({
  type: MOVIES.CHANGE_SEARCH_KEYWORD,
  keyword,
});

export { loadMovies, setMovies, changeSearchKeyword };
