import React, { Fragment, useEffect } from "react";
import { connect, useSelector, useDispatch } from "react-redux";

import Movie from "../Movie/Movie";

import {
  fetchPopularMovies,
  changeSearchKeyword,
  fetchMoviesByKeyword,
} from "../../actions";

const BASE_IMAGE_PATH = "https://image.tmdb.org/t/p/w500";

const posterPath = (path) => {
  return `${BASE_IMAGE_PATH}/${path}`;
};

const Movies = () => {
  useEffect(() => {
    // fetchPopularMovies();
  }, []);

  const keyword = useSelector((state) => state.keyword);
  const movies = useSelector((state) => state.movies);

  const dispatch = useDispatch();

  return (
    <Fragment>
      <div>a movie</div>
      keyword is {keyword} ----
      <button onClick={() => dispatch(fetchMoviesByKeyword, "hello")}>
        keyword change
      </button>
      <button onClick={() => dispatch(fetchMoviesByKeyword)}>by keyword</button>
      {console.log(movies)}
      {movies.length && (
        <Movie
          title={movies[0].original_title}
          poster={posterPath(movies[0].poster_path)}
        />
      )}
    </Fragment>
  );
};

const mapDispatchToProps = (dispatch) => ({
  fetchPopularMovies: () => dispatch(fetchPopularMovies()),
  fetchMoviesByKeyword: (keyword) => dispatch(fetchMoviesByKeyword(keyword)),
  changeSearchKeyword: (keyword) => dispatch(changeSearchKeyword(keyword)),
});
