import { call, takeEvery, put } from "redux-saga/effects";

import { MOVIES } from "../constants";
import { setMovies } from "../actions";

import { fetchMovieByID, fetchPopularMovies } from "../api/index";

export function* handleMoviesLoad(movieFetchFunc) {
  const movies = yield call(movieFetchFunc);
  yield put(setMovies(movies));
}

export default function* watchMoviesLoad() {
  yield takeEvery(MOVIES.FETCH, () =>
    handleMoviesLoad(() => fetchMovieByID(550))
  );
}
