import React, { Fragment, useEffect } from "react";
import { connect } from "react-redux";

import {
  fetchPopularMovies,
  changeSearchKeyword,
  fetchMoviesByKeyword,
} from "../actions";
// import { getMovie } from "../selectors";

const Movie = ({
  fetchPopularMovies,
  fetchMoviesByKeyword,
  changeSearchKeyword,
  movies,
  isLoading,
  error,
  keyword,
}) => {
  useEffect(() => {
    // fetchPopularMovies();
    fetchMoviesByKeyword("world");
  }, []);

  return (
    <Fragment>
      <div>a movie</div>
      keyword is {keyword} ----
      {movies && JSON.stringify(movies)}
      {/*<button onClick={() => console.log("hello", movies)}>hello</button>*/}
      <button onClick={() => changeSearchKeyword("hello")}>
        keyword change
      </button>
      <button onClick={() => fetchMoviesByKeyword(keyword)}>by keyword</button>
    </Fragment>
  );
};

const mapStateToProps = ({ isLoading, movies, error, keyword }) => ({
  movies,
  isLoading,
  error,
  keyword,
});

const mapDispatchToProps = (dispatch) => ({
  fetchPopularMovies: () => dispatch(fetchPopularMovies()),
  fetchMoviesByKeyword: (keyword) => dispatch(fetchMoviesByKeyword(keyword)),
  changeSearchKeyword: (keyword) => dispatch(changeSearchKeyword(keyword)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Movie);
